INSERT INTO dancesteps(steps) VALUES ('Step 1: Start with your feet shoulder width apart and relaxed');
INSERT INTO dancesteps(steps) VALUES ('Step 2: Criss cross your feet and go back to starting position');
INSERT INTO dancesteps(steps) VALUES ('Step 3: Bend your right leg behind you and touch your left hand to your right foot and back to starting position');
INSERT INTO dancesteps(steps) VALUES ('Step 4: Lean with it, rock with it...SNAP(your fingers), back to starting position');
INSERT INTO dancesteps(steps) VALUES ('Step 5: Cross your right foot in front of your left, then out to the right. Then pick your right knee up and slap it...back to starting position');
INSERT INTO dancesteps(steps) VALUES ('Step 6: Cross your arms over your chest and push your body forward, with both arms and right leg back like you are flying like Superman');
INSERT INTO dancesteps(steps) VALUES ('Step 7: Lean back towards your left, with both arms extended towards your right side. Hop back towards left, on left leg');
INSERT INTO dancesteps(steps) VALUES ('Step 8: Repeat Step 7, but reverse all movements');